function RetnYMD() {
        var date=new Date(); 
        var year=date.getFullYear();
        var month=date.getMonth() + 1; 
        var strDate=date.getDate();
        var hour=date.getHours();
        var minu=date.getMinutes();
		var randomShu=Math.floor(Math.random() * (20 - 5)) + 5
		var minu2=date.getMinutes()-randomShu;
        var sec=date.getSeconds();
        if (month<10) month="0"+month; 
        if (strDate<10) strDate="0"+strDate;
        //if (hour<10) hour="0"+hour;
        if (minu<10) minu="0"+minu;
//        if (sec<10) sec="0"+sec;
//        var YMDdate=year+"-"+month+"-"+strDate+" "+hour+":"+minu+":"+sec;
        var YMDdate=year+"年"+month+"月"+strDate+"日 "+hour+":"+minu;
		
		if (minu2<0){
			minu2=minu2+60;
			hour=hour-1;
		}
		if (hour<10) hour="0"+hour;
                           //     if (minu<10) minu="0"+minu;
		if (minu2<10) minu2="0"+minu2;
		// var YMDdate=year+"年"+month+"月"+strDate+"日 "+hour+":"+minu;
		var YMDdatePatient=year+"年"+month+"月"+strDate+"日 "+hour+":"+minu2;
        return [YMDdate,YMDdatePatient]
    }

